function googleTranslateElementInit(bahasa) {
    new google.translate.TranslateElement({ 
        pageLanguage: bahasa, 
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE, 
        autoDisplay: false }, 'google_translate_element');
}

$('#btn_flag').on('click',function(){
    var value = $('#btn_flag').val();
    googleTranslateElementInit(value);
    var $frame = $('.goog-te-menu-frame:first');
    if (!$frame.size()) {
        alert("Error: Tidak dapat mencari terjemahan.");
        return false;
    }

    $frame.contents().find('.goog-te-menu2-item span.text:contains(' + value + ')').get(0).click();
    return false;
});

